        [Fact]
        public void Colorize()
        {
            var Colors = new List<Color>();
            for (int x = 0; x < 256; ++x)
                Colors.Add(Color.FromArgb(255 - x, 0, x));
            using (Utilities.Media.SwiftBitmap TestObject = new Utilities.Media.SwiftBitmap(@"..\..\Data\Image\Lenna.jpg").BlackAndWhite())
            {
                TestObject.Colorize(Colors.ToArray()).Save(@".\Testing\LennaColorize.jpg");
                Assert.True(new Utilities.IO.FileInfo(@".\Testing\LennaColorize.jpg").ReadBinary().SequenceEqual(new Utilities.IO.FileInfo(@"..\..\BitmapResults\LennaColorize.jpg").ReadBinary()));
